import React, { useEffect, useState } from "react";
import styles from "./Restaurants.module.css";
import Restaurant from "./Restaurant/Restaurant";
import restaurantsData from "../../services/__mocks__/restaurantsData";
import * as ReactBootStrap from "react-bootstrap";
 
const Restaurants = () => {
  const [restaurants, setRestaurants] = useState([]);
  const [loaded, setLoaded] = useState(false);
  const [searchInput, setSearchInput] = useState("");
 
  const getRestaurants = async () => {
    try {
      console.log("Fetching restaurants...");
      const response = await restaurantsData;
      console.log(response.data);
      const restaurantsData = [];
      (response.data.data).forEach((item) => {
        restaurantsData.push(item);
      });
      setRestaurants(restaurantsData);
      setLoaded(true);
    } catch (e) {
      console.log(e);
    }
  }
 
  useEffect(() => {
    getRestaurants();
  }, []);
 
  return (
    <div className={styles.restaurants__container}>
      <div className={styles.filter__container}>
        <input className={styles.filter} type="text" placeholder="Search Restaurant" value={searchInput}
          onChange={event => setSearchInput(event.target.value)} />
      </div>
      <div className={styles.restaurants}>
        {
          loaded ?
            restaurants.filter(restaurant => restaurant.name.toLowerCase().includes(searchInput.toLowerCase()) || searchInput === "")
              .map(restaurant => (
                <Restaurant key={restaurant.id} restaurant={restaurant} />
              ))
            : (<ReactBootStrap.Spinner animation="border" />)
        }
      </div>
    </div>
  );
};
 
export default Restaurants;
